import React from 'react';
import {connect} from 'react-redux';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

import './sign-in.styles.scss';

import {googleSignInStart, emailSignInStart} from '../../redux/user/user.actions';


class SignIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: ''
        }
    }

    handleSubmit = async event => {
        event.preventDefault();

        const {emailSignInStart} = this.props;
        const {email,password} = this.state;

        emailSignInStart(email, password);

    }

    handleChange = (event) => {
        const {value, name} = event.target;
        this.setState({[name]: value});
    }

    render() {
        const {googleSignInStart} = this.props
        return (
            <div className='sign-in'>
                <h2>l already have an Account</h2>
                <span>Sign in with Email and Password</span>

                <form onSubmit={this.handleSubmit}>
                    <FormInput type='email' value={this.state.email} handleChange={this.handleChange} name='email' label="email" required/>
                   

                    <FormInput type='password' value={this.state.password} handleChange={this.handleChange} label="password" name='password' required/>
                   
                    <div className='buttons'>
                        <CustomButton type='submit'>Sign In </CustomButton>
                        <CustomButton type='button' onClick={googleSignInStart} isGoogleSignIn >Sign in With Google </CustomButton>
                    </div>
                </form>
            </div>
        )
    }
}

const mapDispatchToProps = dispatch =>  ({
    googleSignInStart: () => dispatch(googleSignInStart()),
    emailSignInStart: (email, password) => dispatch(emailSignInStart({email, password}))
})
export default connect(null, mapDispatchToProps)(SignIn);
