import React from 'react';

import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

import './sign-in.styles.scss';
import {signInWithGoogle} from '../../firebase/firebase.utils';

class SignIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: ''
        }
    }

    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({password:'', email:''});

    }

    handleChange = (event) => {
        const {value, name} = event.target;
        this.setState({[name]: value});
    }

    render() {
        return (
            <div className='sign-in'>
                <h2>l already have an Account</h2>
                <span>Sign in with Email and Password</span>

                <form onSubmit={this.handleSubmit}>
                    <FormInput type='email' value={this.state.email} handleChange={this.handleChange} name='email' label="email" required/>
                   

                    <FormInput type='password' value={this.state.password} handleChange={this.handleChange} label="password" name='password' required/>
                   

                    <CustomButton type='submit'>Sign In </CustomButton>
        <CustomButton onClick={signInWithGoogle} isGoogleSignIn>{''}Sign in With Google{''}</CustomButton>
                </form>
            </div>
        )
    }
}

export default SignIn;
